import React, { Component } from 'react'
import './App.css';
import { Weather } from './Weather.js';
import "bootstrap/dist/css/bootstrap.min.css";
import "weather-icons/css/weather-icons.css"

// api.openweathermap.org/data/2.5/weather?q=London,uk&appid={API key}
const API_Key="e4ae41adf2c5e12fd01e7d271fbf4d54";


class App extends Component {
  constructor() {
    super()

    this.state = {
      city:undefined,
      country: undefined,
      icon: undefined,
      main: undefined,
      celsius: undefined,
      Temp_max: undefined,
      Temp_min: undefined,
      Description: "",
      error:false 
    }
    this.Getweather();
  }

  calcCelsius(temp)
  {
    let cel=Math.floor(temp-273.5);
    return cel;
  }

  Getweather= async() =>{
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=${API_Key}`)
    const response = await api_call.json();
    console.log(response);

    this.setState({
      country:response.sys.country,
      city:response.name,
      celsius:this.calcCelsius(response.main.temp),
      Temp_min: this.calcCelsius(response.main.temp_min),
      Temp_max: this.calcCelsius(response.main.temp_max),
      Description: response.Weather[0].Description

    })
  }
  render() {
    return (
      <div className="App">
          <Weather country={this.state.country} city={this.state.city}
          celsius={this.state.celsius}
          Temp_min={this.state.Temp_min}
          Temp_max={this.state.Temp_max}
          Description={this.state.Description}
          />
      </div>
    )
  }
}



export default App;
